parameters.text:
={
  "prompt": {
    "role": "Extractor de Datos",
    "description": "Extrae los datos de consumo mensual en kWh desde una tabla de texto plano como la que aparece en una factura CFE. Busca tablas con columnas de período, demanda kW, consumo kWh, factor potencia, etc.",
    "input_data": "{{ $json.pages[0].markdown }}",
    "rules": [
      {
        "rule": "Busca tablas que contengan columnas como 'Periodo', 'Consumo total kWh', 'Demanda kW', etc."
      },
      {
        "rule": "Extrae SOLO el período (mes y año) y el consumo en kWh de cada fila."
      },
      {
        "rule": "El período debe estar en formato MES + año de 2 dígitos (ej: JUN24, FEB25, DIC24)."
      },
      {
        "rule": "Si el mes aparece como 3 letras (JUN, FEB, DIC), manténlo así. Si aparece completo, abrevia a 3 letras en español."
      },
      {
        "rule": "El consumo debe ser solo el número entero de kWh, sin comas ni símbolos."
      },
      {
        "rule": "Si hay filas duplicadas del mismo mes con diferentes consumos, mantén ambas por separado."
      },
      {
        "rule": "Ignora encabezados, totales, promedios y cualquier dato que no sea un período específico."
      },
      {
        "rule": "Si el consumo contiene comas (ej: 42,842), elimina las comas (42842)."
      },
      {
        "rule": "Ordena los datos cronológicamente del más antiguo al más reciente."
      },
      {
        "rule": "NO inventes valores. Usa SOLO los que están claramente indicados en la tabla."
      },
      {
        "rule": "El resultado debe ser ÚNICAMENTE JSON válido, sin texto adicional, explicaciones ni comentarios."
      },

    ],
    "output_example": {
      "consumo_historico": [
        {"periodo": "JUN24", "kwh": 42842},
        {"periodo": "JUL24", "kwh": 56355},
        {"periodo": "AGO24", "kwh": 60341}
      ],
      "consumo_max": 62988
    },
    "response_format": "JSON"
  }
}

parameters.functionCode:
// OPCIÓN 1: Hash simple sin crypto (RECOMENDADA)
// Ahora accedemos correctamente a los datos del nodo anterior 'Set'
const lat = $json.latitude || 0;
const lng = $json.longitude || 0;
const radioKm = 5;

// Verificar que tenemos coordenadas válidas
if (!lat || !lng) {
  throw new Error('No se encontraron coordenadas válidas. Latitude: ' + lat + ', Longitude: ' + lng);
}

// Crear grid redondeado para optimizar caché
const latGrid = Math.round(lat * 100) / 100;
const lngGrid = Math.round(lng * 100) / 100;

// Hash simple pero efectivo
function createLocationHash(lat, lng) {
  const str = `${lat},${lng}`;
  let hash = 0;

  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }

  return Math.abs(hash).toString(36);
}

const hashUbicacion = createLocationHash(latGrid, lngGrid);

return [{
  json: {
    latitude: lat,
    longitude: lng,
    lat_grid: latGrid,
    lng_grid: lngGrid,
    radio_km: radioKm,
    hash_ubicacion: hashUbicacion
  }
}];

// Fusionar los datos de irradiación con la información de caché guardado
const irradiacionData = $node["Calculate Annual Irradiation1"].json;
const cacheInfo = $json[0] || {};

// Obtener temp_lead_id desde el nodo Code1 donde se genera originalmente
let tempLeadId = 'unknown';

try {
  // Acceder directamente al nodo Code1 que genera el temp_lead_id
  const code1Items = $node["Code1"].json;
  if (Array.isArray(code1Items)) {
    tempLeadId = code1Items[0]?.temp_lead_id || 'unknown';
  } else if (code1Items?.temp_lead_id) {
    tempLeadId = code1Items.temp_lead_id;
  }
} catch (error) {
  console.error('Error getting temp_lead_id:', error);
  tempLeadId = 'lead_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
}

return [{
  json: {
    ...irradiacionData,
    cache_saved: true,
    cache_id: cacheInfo.id,
    hash_ubicacion: cacheInfo.hash_ubicacion,
    temp_lead_id: tempLeadId
  }
}];

