parameters.functionCode:
const data = $json?.properties?.parameter?.ALLSKY_SFC_SW_DWN ?? {};
const keys = Object.keys(data);

if (keys.length === 0) {
  return {
    datos_nasa_mensuales: {
      irradiacion_promedio: [],
      unidad_medida: "kWh/m²/día",
      resumen: {
        mejor_mes: null,
        peor_mes: null
      }
    },
    irradiacion_promedio_min: null,
    irradiacion_promedio_max: null,
    irradiacion_promedio_anual: null
  };
}

// Mapeo de meses con orden para mantener secuencia correcta
const monthsMap = [
  { numero: "01", nombre: "Enero",      orden: 1 },
  { numero: "02", nombre: "Febrero",    orden: 2 },
  { numero: "03", nombre: "Marzo",      orden: 3 },
  { numero: "04", nombre: "Abril",      orden: 4 },
  { numero: "05", nombre: "Mayo",       orden: 5 },
  { numero: "06", nombre: "Junio",      orden: 6 },
  { numero: "07", nombre: "Julio",      orden: 7 },
  { numero: "08", nombre: "Agosto",     orden: 8 },
  { numero: "09", nombre: "Septiembre", orden: 9 },
  { numero: "10", nombre: "Octubre",    orden: 10 },
  { numero: "11", nombre: "Noviembre",  orden: 11 },
  { numero: "12", nombre: "Diciembre",  orden: 12 }
];

// Acumuladores
const sumPerMonth = {};
const countPerMonth = {};
let totalSum = 0;
let totalCount = 0;

// Procesar datos - excluir mes 13 (promedios anuales)
for (const k of keys) {
  const v = +data[k];
  if (!Number.isFinite(v)) continue;

  const month = k.slice(4, 6); // YYYYMM...

  // Saltar mes 13 (promedios anuales en los datos originales)
  if (month === "13") continue;

  if (!sumPerMonth[month]) {
    sumPerMonth[month] = 0;
    countPerMonth[month] = 0;
  }

  sumPerMonth[month] += v;
  countPerMonth[month] += 1;
  totalSum += v;
  totalCount += 1;
}

// Calcular promedio anual
const promedioAnual = totalCount > 0 ? +(totalSum / totalCount).toFixed(2) : null;

// Crear array de irradiación promedio (ordenado por mes)
const irradiacionPromedio = monthsMap
  .filter(m => m.numero in sumPerMonth) // Solo meses con datos
  .map(m => {
    const avg = sumPerMonth[m.numero] / countPerMonth[m.numero];
    return {
      mes: m.nombre,
      irradiacion: +avg.toFixed(2),
      orden: m.orden,
      mes_numero: m.numero
    };
  })
  .sort((a, b) => a.orden - b.orden); // Ordenar por mes natural

// Calcular min y max de los promedios mensuales
const irradiacionValues = irradiacionPromedio.map(x => x.irradiacion);
const minIrradiacion = irradiacionValues.length > 0 ? Math.min(...irradiacionValues) : null;
const maxIrradiacion = irradiacionValues.length > 0 ? Math.max(...irradiacionValues) : null;

// Estructura para datos_nasa_mensuales (JSONB) - formato simplificado
const datosNasaMensuales = {
  irradiacion_promedio: irradiacionPromedio,
  unidad_medida: "kWh/m²/día",
  resumen: {
    mejor_mes: irradiacionPromedio.length > 0 
      ? irradiacionPromedio.reduce((max, curr) => 
          curr.irradiacion > max.irradiacion ? curr : max
        ).mes 
      : null,
    peor_mes: irradiacionPromedio.length > 0 
      ? irradiacionPromedio.reduce((min, curr) => 
          curr.irradiacion < min.irradiacion ? curr : min
        ).mes 
      : null
  }
};

// Retornar objeto con los campos exactos para la base de datos
return {
  datos_nasa_mensuales: datosNasaMensuales,
  irradiacion_promedio_min: minIrradiacion,
  irradiacion_promedio_max: maxIrradiacion,
  irradiacion_promedio_anual: promedioAnual
};

parameters.jsCode:
/*****************************************************
 * PV Sizing (mínimo-insumos, días/mes fijo = 30.4)
 * Usa SOLO:
 *   • output.consumo_max ............ kWh/mes (pico consumo histórico CFE)
 *   • irradiacion_promedio_anual ..... kWh/m²/día (NASA promedio)
 *****************************************************/

//–––– Parámetros ajustables ––––
const PR            = 0.80;
const oversizeFact  = 1.05;
const panelWp_def   = 550;
const DAYS_PER_MONTH = 30.4;

//–––– Entrada ––––
const j = items[0].json || {};

// Preservar temp_lead_id
const tempLeadId = j.temp_lead_id || 'unknown';

// $input.first().json.output.consumo_max
const kWhPeak = +j.output?.consumo_max || 0;
if (!(kWhPeak > 0)) {
  throw new Error('Falta output.consumo_max (>0).');
}
const kWhYear = kWhPeak * 12;

// $input.first().json.irradiacion_promedio_anual (CORREGIDO)
const irrDayAvg = +j.irradiacion_promedio_anual || NaN;
if (!(irrDayAvg > 0)) {
  throw new Error('Falta irradiacion_promedio_anual (>0).');
}

//–––– Rendimientos ––––
const specDayAvg = irrDayAvg * PR;
const specYieldMonth = specDayAvg * DAYS_PER_MONTH;
const specYieldAnnual = specDayAvg * 365;

//–––– Potencia requerida ––––
const kWpByYear = kWhYear / specYieldAnnual;
const kWpByPeak = kWhPeak / specYieldMonth;
const kWpNeededRaw = Math.max(kWpByYear, kWpByPeak);
const kWpNeeded = +(kWpNeededRaw * oversizeFact).toFixed(2);

//–––– Módulos ––––
const panelWp = +j.panel_wp > 0 ? +j.panel_wp : panelWp_def;
const nPanels = Math.ceil((kWpNeeded * 1000) / panelWp);

//–––– Producción ––––
const yearlyProd = +(specYieldAnnual * kWpNeeded).toFixed(0);

//–––– Salida ––––
items[0].json = {
  ...j,
  temp_lead_id: tempLeadId, // Asegurar que temp_lead_id se pasa
  sizing: {
    inputs: {
      kWh_peak_month: kWhPeak,
      kWh_year_assumed: kWhYear,
      irr_avg_day: irrDayAvg,
      days_per_month: DAYS_PER_MONTH,
      PR,
      oversize_fact: oversizeFact,
      // Datos NASA disponibles
      irr_min: +j.irradiacion_promedio_min || null,
      irr_max: +j.irradiacion_promedio_max || null,
      nasa_cache_id: j.id || null,
      hash_ubicacion: j.hash_ubicacion || null
    },
    derived: {
      spec_yield_avg_day: +specDayAvg.toFixed(2),
      spec_yield_avg_month: +specYieldMonth.toFixed(2),
      spec_yield_annual: +specYieldAnnual.toFixed(2),
    },
    results: {
      kWp_needed: kWpNeeded,
      panel_wp: panelWp,
      n_panels: nPanels,
      yearly_prod: yearlyProd,
    },
    method_notes:
      "Sizing basado en output.consumo_max (pico mensual) y irradiacion_promedio_anual (kWh/m²/día) de NASA; conversión con 30.4 d/mes fijo; anual = 365×promedio."
  }
};

return items;

