// Asegurar que temp_lead_id se propague
const item = items[0];

// Buscar temp_lead_id en varios lugares posibles
let tempLeadId = null;

// Primero intentar desde el item actual
if (item.json.temp_lead_id) {
  tempLeadId = item.json.temp_lead_id;
}

// Si no está, generar uno nuevo
if (!tempLeadId || tempLeadId === 'unknown') {
  tempLeadId = 'lead_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
}

// Asegurar que todos los datos se pasen con el temp_lead_id
return [{
  json: {
    ...item.json,
    temp_lead_id: tempLeadId
  },
  binary: item.binary
}];

parameters.jsCode:
const items = [];

// Obtener el item de entrada
const inputItem = $input.all()[0];

// Generar temp_lead_id único
const tempLeadId = `lead_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// Crear item para data-frontal
if (inputItem.binary['data-frontal']) {
  items.push({
    json: { type: 'frontal', temp_lead_id: tempLeadId },
    binary: { 'data-frontal': inputItem.binary['data-frontal'] }
  });
}

// Crear item para data-posterior  
if (inputItem.binary['data-posterior']) {
  items.push({
    json: { type: 'posterior', temp_lead_id: tempLeadId },
    binary: { 'data-posterior': inputItem.binary['data-posterior'] }
  });
}

// Si no hay binarios, crear item básico con temp_lead_id
if (items.length === 0) {
  items.push({
    json: { type: 'empty', temp_lead_id: tempLeadId }
  });
}

return items;
