// OPCIÓN 1: Hash simple sin crypto (RECOMENDADA)
// Ahora accedemos correctamente a los datos del nodo anterior 'Set'
const lat = $json.latitude || 0;
const lng = $json.longitude || 0;
const radioKm = 5;

// Verificar que tenemos coordenadas válidas
if (!lat || !lng) {
  throw new Error('No se encontraron coordenadas válidas. Latitude: ' + lat + ', Longitude: ' + lng);
}

// Crear grid redondeado para optimizar caché
const latGrid = Math.round(lat * 100) / 100;
const lngGrid = Math.round(lng * 100) / 100;

// Hash simple pero efectivo
function createLocationHash(lat, lng) {
  const str = `${lat},${lng}`;
  let hash = 0;

  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }

  return Math.abs(hash).toString(36);
}

const hashUbicacion = createLocationHash(latGrid, lngGrid);

return [{
  json: {
    latitude: lat,
    longitude: lng,
    lat_grid: latGrid,
    lng_grid: lngGrid,
    radio_km: radioKm,
    hash_ubicacion: hashUbicacion
  }
}];

// Fusionar los datos de irradiación con la información de caché guardado
const irradiacionData = $node["Calculate Annual Irradiation1"].json;
const cacheInfo = $json[0] || {};

// Obtener user_id desde el nodo Code1 donde se genera originalmente
let userId = 'unknown';
let projectTitle = 'Proyecto Solar';
let projectDescription = '';

try {
  // Acceder directamente al nodo Code1 que genera el user_id
  const code1Items = $node["Code1"].json;
  if (Array.isArray(code1Items) && code1Items.length > 0) {
    userId = code1Items[0]?.user_id || 'unknown';
    projectTitle = code1Items[0]?.project_title || 'Proyecto Solar';
    projectDescription = code1Items[0]?.project_description || '';
  } else if (code1Items && typeof code1Items === 'object') {
    userId = code1Items.user_id || 'unknown';
    projectTitle = code1Items.project_title || 'Proyecto Solar';
    projectDescription = code1Items.project_description || '';
  }
} catch (error) {
  console.error('Error getting user_id from Code1:', error);
  userId = 'unknown';
}

console.log('Merge Cache Info - userId:', userId);
console.log('Merge Cache Info - projectTitle:', projectTitle);

return [{
  json: {
    ...irradiacionData,
    cache_saved: true,
    cache_id: cacheInfo.id,
    hash_ubicacion: cacheInfo.hash_ubicacion,
    user_id: userId,
    project_title: projectTitle,
    project_description: projectDescription
  }
}];

