/*****************************************************
 * PV Sizing (mínimo-insumos, días/mes fijo = 30.4)
 * Usa SOLO:
 *   • output.consumo_max ............ kWh/mes (pico consumo histórico CFE)
 *   • irradiacion_promedio_anual ..... kWh/m²/día (NASA promedio)
 *****************************************************/

//–––– Parámetros ajustables ––––
const PR            = 0.80;
const oversizeFact  = 1.05;
const panelWp_def   = 550;
const DAYS_PER_MONTH = 30.4;

//–––– Entrada ––––
const j = items[0].json || {};

// Preservar user_id y project data
const userId = j.user_id || 'unknown';
const projectTitle = j.project_title || 'Proyecto Solar';
const projectDescription = j.project_description || '';

// $input.first().json.output.consumo_max
const kWhPeak = +j.output?.consumo_max || 0;
if (!(kWhPeak > 0)) {
  throw new Error('Falta output.consumo_max (>0).');
}
const kWhYear = kWhPeak * 12;

// $input.first().json.irradiacion_promedio_anual (CORREGIDO)
const irrDayAvg = +j.irradiacion_promedio_anual || NaN;
if (!(irrDayAvg > 0)) {
  throw new Error('Falta irradiacion_promedio_anual (>0).');
}

//–––– Rendimientos ––––
const specDayAvg = irrDayAvg * PR;
const specYieldMonth = specDayAvg * DAYS_PER_MONTH;
const specYieldAnnual = specDayAvg * 365;

//–––– Potencia requerida ––––
const kWpByYear = kWhYear / specYieldAnnual;
const kWpByPeak = kWhPeak / specYieldMonth;
const kWpNeededRaw = Math.max(kWpByYear, kWpByPeak);
const kWpNeeded = +(kWpNeededRaw * oversizeFact).toFixed(2);

//–––– Módulos ––––
const panelWp = +j.panel_wp > 0 ? +j.panel_wp : panelWp_def;
const nPanels = Math.ceil((kWpNeeded * 1000) / panelWp);

//–––– Producción ––––
const yearlyProd = +(specYieldAnnual * kWpNeeded).toFixed(0);

//–––– Salida ––––
items[0].json = {
  ...j,
  user_id: userId, // Asegurar que user_id se pasa
  project_title: projectTitle,
  project_description: projectDescription,
  sizing: {
    inputs: {
      kWh_peak_month: kWhPeak,
      kWh_year_assumed: kWhYear,
      irr_avg_day: irrDayAvg,
      days_per_month: DAYS_PER_MONTH,
      PR,
      oversize_fact: oversizeFact,
      // Datos NASA disponibles
      irr_min: +j.irradiacion_promedio_min || null,
      irr_max: +j.irradiacion_promedio_max || null,
      nasa_cache_id: j.id || null,
      hash_ubicacion: j.hash_ubicacion || null
    },
    derived: {
      spec_yield_avg_day: +specDayAvg.toFixed(2),
      spec_yield_avg_month: +specYieldMonth.toFixed(2),
      spec_yield_annual: +specYieldAnnual.toFixed(2),
    },
    results: {
      kWp_needed: kWpNeeded,
      panel_wp: panelWp,
      n_panels: nPanels,
      yearly_prod: yearlyProd,
    },
    method_notes:
      "Sizing basado en output.consumo_max (pico mensual) y irradiacion_promedio_anual (kWh/m²/día) de NASA; conversión con 30.4 d/mes fijo; anual = 365×promedio."
  }
};

return items;

