const items = [];

// Obtener el item de entrada
const inputItem = $input.all()[0];

// Buscar user_id en múltiples formatos desde el webhook
let userId = null;

// Primero intentar desde el body (FormData) - AQUÍ ESTÁ EL CAMBIO
if (inputItem.json.body && inputItem.json.body.user_id) {
  userId = inputItem.json.body.user_id;
} else if (inputItem.json.body && inputItem.json.body.userId) {
  userId = inputItem.json.body.userId;
} else if (inputItem.json.body && inputItem.json.body.usuario_id) {
  userId = inputItem.json.body.usuario_id;
} else if (inputItem.json.user_id) {
  userId = inputItem.json.user_id;
} else if (inputItem.json.userId) {
  userId = inputItem.json.userId;
} else if (inputItem.json.usuario_id) {
  userId = inputItem.json.usuario_id;
}

// Si no está en el body, intentar desde query parameters
if (!userId && inputItem.json.query) {
  userId = inputItem.json.query.user_id;
}

// Último intento: desde headers
if (!userId && inputItem.json.headers && inputItem.json.headers['user-id']) {
  userId = inputItem.json.headers['user-id'];
}

const projectTitle = (inputItem.json.body && inputItem.json.body.project_title) || inputItem.json.project_title || 'Proyecto Solar';
const projectDescription = (inputItem.json.body && inputItem.json.body.project_description) || inputItem.json.project_description || '';

console.log('Debug - Input item:', JSON.stringify(inputItem.json, null, 2));
console.log('Debug - Found user_id:', userId);

if (!userId) {
  throw new Error('user_id is required for nuevo proyecto workflow - received: ' + JSON.stringify(inputItem.json));
}

// Crear item para data-frontal
if (inputItem.binary && inputItem.binary['data-frontal']) {
  items.push({
    json: { 
      type: 'frontal', 
      user_id: userId,
      project_title: projectTitle,
      project_description: projectDescription
    },
    binary: { 'data-frontal': inputItem.binary['data-frontal'] }
  });
}

// Crear item para data-posterior  
if (inputItem.binary && inputItem.binary['data-posterior']) {
  items.push({
    json: { 
      type: 'posterior', 
      user_id: userId,
      project_title: projectTitle,
      project_description: projectDescription
    },
    binary: { 'data-posterior': inputItem.binary['data-posterior'] }
  });
}

// Si no hay binarios, crear item básico con user_id
if (items.length === 0) {
  items.push({
    json: { 
      type: 'empty', 
      user_id: userId,
      project_title: projectTitle,
      project_description: projectDescription
    }
  });
}

console.log('Debug - Created items:', items.length);
return items;
